import React, { useState } from 'react'
import { Text, View, StyleSheet } from "react-native"
import TextBox from "../components/TextBox"
import Btn from "../components/Btn"
import firebase from 'firebase/app';
import "firebase/auth";
import { TouchableOpacity } from 'react-native-web';

const styles = StyleSheet.create({
    view: {
        flex: 1,
        width: "100%",
        justifyContent: "center",
        alignItems: "center"
    },
    btn: {
        height: 42,
        width: "48%",
        borderRadius: 25,
        marginTop: 20,
        backgroundColor: "#0B3270",
        justifyContent: "center",
        alignItems: "center",
    },
})

export default function Loginscreen({ navigation }) {

    const [values, setValues] = useState({
        email: "",
        pwd: ""
    })

    function handleChange(text, eventName) {
        setValues(prev => {
            return {
                ...prev,
                [eventName]: text
            }
        })
    }

    function Login() {

        const { email, pwd } = values

        firebase.auth().signInWithEmailAndPassword(email, pwd)
            .then(() => {
            })
            .catch((error) => {
                alert(error.message)
                // ..
            });
    }

    return <View style={styles.view}>
        <Text style={{ fontSize: 34, fontWeight: "800", marginBottom: 20 }}>Login</Text>
        <TextBox placeholder="Email Address" onChangeText={text => handleChange(text, "email")} />
        <TextBox placeholder="Password" onChangeText={text => handleChange(text, "pwd")} secureTextEntry={true} />
        <View style={{ flexDirection: "row", justifyContent: "space-between", alignItems: "center", width: "92%", }}>
           <TouchableOpacity>
            <Btn onClick={() => Login()} title="Login" style={{
                height: 42,
                width: "48%",
                borderRadius: 25,
                marginTop: 20,
                backgroundColor: "#0B3270",
                justifyContent: "center",
                alignItems: "center",
            }} />
            <Btn onClick={() => navigation.navigate("Sign Up")} title="Sign Up" style={{ 
                height: 42,
                width: "48%",
                borderRadius: 25,
                marginTop: 20,
                backgroundColor: "#0B3270",
                justifyContent: "center",
                alignItems: "center",
             }} />
             </TouchableOpacity>
        </View>
    </View>
}
