import React, { useState, useEffect } from 'react'
import { Text, View, StyleSheet } from "react-native"
import Btn from "../components/Btn"
import firebase from 'firebase/app';
import "firebase/auth";
import SendMoney from './SendMoney';

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
        padding: 20,
        alignItems: 'center',
        justifyContent: 'center',
      },
      headerText: {
        fontSize: 24,
        fontWeight: 'bold',
        color: '#333333',
        marginBottom: 20,
      },
      balanceContainer: {
        backgroundColor: 'white',
        padding: 20,
        paddingHorizontal: 100,
        borderRadius: 10,
        shadowColor: '#000',
        shadowOffset: {
          width: 0,
          height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5,
        alignItems: 'center',
      },
      balanceLabel: {
        fontSize: 16,
        fontWeight: 'bold',
            marginBottom: 10,
        color: '#555555',
      },
      balanceAmount: {
        fontSize: 24,
        fontWeight: 'bold',
        color: '#333333',
        marginBottom: 10,
      },
      conversionRate: {
        fontSize: 12,
        color: '#777777',
        marginBottom: 10,
      },
})



export default function Loginscreen({navigation}) {
    return (
        <View style={styles.container}>
        <Text style={styles.headerText}>Welcome, Anthony</Text>
        <View style={styles.balanceContainer}>
        <Text style={styles.balanceLabel}>Current Balance</Text>
        <Text style={styles.balanceAmount}>₱ {currentBalance}</Text>
        <Text style={styles.conversionRate}>1 Wemix = ₱{wemixToPeso}</Text>
        </View>
        <SendMoney currentBalance={currentBalance} updateCurrentBalance={updateCurrentBalance} />
        <Btn title="Log Out" onClick={() => firebase.auth().signOut()} />
    </View>
    );
}
